import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="Parentbox">
      <FotoProduk />
      <ProdukInfo isDiscount="yes" name="Color Pop Chuck" category="CONVERSE" />
      <ReviewItems />
    </div>
  );
}

function ReviewItems() {
  // mempersiapkan data dummy JSON
  const users = [
    {
      "id": 1,
      "image": "person.jpeg",
      "name": "Madison Sthepani",
      "review": "Not bad shit, is a impressive, I like it, good quality."
    },
    {
      "id": 2,
      "image": "https://images.pexels.com/photos/5691309/pexels-photo-5691309.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      "name": "Jett Reyna",
      "review": "Not bad shit, is a impressive, I like it, good quality."
    },
    {
      "id": 3,
      "image": "https://images.pexels.com/photos/1317712/pexels-photo-1317712.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
      "name": "Sage Viper",
      "review": "Not bad shit, is a impressive, I like it, good quality."
    }
  ];
  const listReview = users.map((itemReview) => 
    <div className="Item">
      <img src={itemReview.image}/>
    <div className="User">
        <h3>{itemReview.name}</h3>
        <p>{itemReview.review}</p>
      </div>
    </div>
  );
  return (
    <div className="Review-box">
      <h2>Reviews</h2>
      {listReview}
    </div>
  );
}

function FotoProduk() {
  return (
      <div className="Foto">
        <img src="converse.jpg"/>
      </div>
  );
}

function CheckDiscount(props) {
  const { isDiscount } = props;
  if (isDiscount == "yes") {
    return (
      <p>Diskon 50% Off</p>
    );
  }
  else if (isDiscount == "coming") {
    return (
      <p>Akan Ada Diskon ...</p>
    );
  }
  else {
    return (
      <p>Belum Ada Diskon</p>
    );
  }
}

function ProdukInfo(props) {
  const { category, name, isDiscount } = props;
  const benefits = ["Waterproof Protector", "Color not fade easily", "material not wrinkle easily"];
  const listBenefits = benefits.map((itemBenefit) => 
   <li>{itemBenefit}</li>
  );
  return (
    <div>
      <div className="Deskripsi">
        <p className="Cate">{category}</p>
        <h1 className="Title">{name}</h1>
        <p className="Price">IDR 4.743.999</p>
        <CheckDiscount isDiscount={isDiscount}/>
        <p className="Info">
        Color-popped canvas and rubber overlays bring a new twist to colorblocking on the premium Chuck 70. Soft colors and materials celebrate the game-changing icon, features lightweight, visible cushioning just like the original. One of the most recognizable shoes in the shoes Converse.
        </p>
        <ul>
          {listBenefits}
        </ul>
        <a onClick={(e) => TambahCart(category + " " + name, e )} href="#">Add To Cart</a>
      </div>
    </div>
  );
}

function TambahCart(e) {
  return console.log("Yeay Berhasil Membeli Produk " + e );
}

export default App;
